mapper.update:
UPDATE CD_O_TEMPLATE
SORT_NBR = #{ sortNbr,jdbcType=INTEGER}, STATUS = #{ status,jdbcType=CHAR}, ORIGIN_FLAG = #{ originFlag,jdbcType=CHAR}, ORIGIN_APP = #{ originApp,jdbcType=VARCHAR}, NOTES = #{ notes,jdbcType=VARCHAR}, TEMPLATE_NAME = #{ templateName,jdbcType=VARCHAR}, TEMPLATE_STYLE = #{ templateStyle,jdbcType=VARCHAR}, MODULE_ID = #{ moduleId,jdbcType=INTEGER}, ROLE_TYPE = #{ roleType,jdbcType=VARCHAR}
WHERE ROW_ID = #{ rowId,jdbcType=INTEGER}

update.foreach:
UPDATE CD_O_TEMPLATE
SORT_NBR = #{item.sortNbr,jdbcType=INTEGER}, STATUS = #{item.status,jdbcType=CHAR}, ORIGIN_FLAG = #{item.originFlag,jdbcType=CHAR}, ORIGIN_APP = #{item.originApp,jdbcType=VARCHAR}, NOTES = #{item.notes,jdbcType=VARCHAR}, TEMPLATE_NAME = #{item.templateName,jdbcType=VARCHAR}, TEMPLATE_STYLE = #{item.templateStyle,jdbcType=VARCHAR}, MODULE_ID = #{item.moduleId,jdbcType=INTEGER}, ROLE_TYPE = #{item.roleType,jdbcType=VARCHAR}
WHERE ROW_ID = #{item.rowId,jdbcType=INTEGER}

UPDATE  CD_O_TEMPLATE
SET
DELETED_FLAG  =  '1'  WHERE DELETED_FLAG  =  '0' ${item}

mapper.select:
SELECT 

FROM CD_O_TEMPLATE WHERE DELETED_FLAG='0'  AND
ROW_ID = #{ rowId,jdbcType=INTEGER}
