mapper.update:
UPDATE HR_M_COMPANY
SORT_NBR = #{ sortNbr,jdbcType=INTEGER}, STATUS = #{ status,jdbcType=CHAR}, ORIGIN_FLAG = #{ originFlag,jdbcType=CHAR}, ORIGIN_APP = #{ originApp,jdbcType=VARCHAR}, NOTES = #{ notes,jdbcType=VARCHAR}, COMPANY_MARK = #{ companyMark,jdbcType=VARCHAR}, COMPANY_CODE = #{ companyCode,jdbcType=VARCHAR}, COMPANY_NAME = #{ companyName,jdbcType=VARCHAR}, SORT_NAME = #{ sortName,jdbcType=VARCHAR}, COMPANY_NAME_EN = #{ companyNameEn,jdbcType=VARCHAR}, IS_BLOC = #{ isBloc,jdbcType=VARCHAR}, SUP_COMPANY_ID = #{ supCompanyId,jdbcType=INTEGER}, COMPANY_CLASS = #{ companyClass,jdbcType=VARCHAR}, COMPANY_TYPE = #{ companyType,jdbcType=VARCHAR}, CUSSUP_TYPE = #{ cussupType,jdbcType=CHAR}, COMPANY_SCALE = #{ companyScale,jdbcType=VARCHAR}, TRACE_SOURCE = #{ traceSource,jdbcType=VARCHAR}, TRACE_PEOPLE = #{ tracePeople,jdbcType=VARCHAR}, TP_CELL = #{ tpCell,jdbcType=VARCHAR}, LOGO = #{ logo,jdbcType=LONGVARCHAR}, PROVINCE = #{ province,jdbcType=VARCHAR}, CITY = #{ city,jdbcType=VARCHAR}, COUNTY = #{ county,jdbcType=VARCHAR}, COMPANY_ADDRESS = #{ companyAddress,jdbcType=VARCHAR}, ID_CARD = #{ idCard,jdbcType=VARCHAR}, COMPANY_PHOTO = #{ companyPhoto,jdbcType=LONGVARCHAR}, LONGITUDE = #{ longitude,jdbcType=DECIMAL}, LATITUDE = #{ latitude,jdbcType=DECIMAL}, AREA = #{ area,jdbcType=DECIMAL}, REGISTER_TIME = #{ registerTime,jdbcType=TIMESTAMP}, REGISTER_IP = #{ registerIp,jdbcType=VARCHAR}, OPEN_TIME = #{ openTime,jdbcType=TIMESTAMP}
WHERE ROW_ID = #{ rowId,jdbcType=INTEGER}

update.foreach:
UPDATE HR_M_COMPANY
SORT_NBR = #{item.sortNbr,jdbcType=INTEGER}, STATUS = #{item.status,jdbcType=CHAR}, ORIGIN_FLAG = #{item.originFlag,jdbcType=CHAR}, ORIGIN_APP = #{item.originApp,jdbcType=VARCHAR}, NOTES = #{item.notes,jdbcType=VARCHAR}, COMPANY_MARK = #{item.companyMark,jdbcType=VARCHAR}, COMPANY_CODE = #{item.companyCode,jdbcType=VARCHAR}, COMPANY_NAME = #{item.companyName,jdbcType=VARCHAR}, SORT_NAME = #{item.sortName,jdbcType=VARCHAR}, COMPANY_NAME_EN = #{item.companyNameEn,jdbcType=VARCHAR}, IS_BLOC = #{item.isBloc,jdbcType=VARCHAR}, SUP_COMPANY_ID = #{item.supCompanyId,jdbcType=INTEGER}, COMPANY_CLASS = #{item.companyClass,jdbcType=VARCHAR}, COMPANY_TYPE = #{item.companyType,jdbcType=VARCHAR}, CUSSUP_TYPE = #{item.cussupType,jdbcType=CHAR}, COMPANY_SCALE = #{item.companyScale,jdbcType=VARCHAR}, TRACE_SOURCE = #{item.traceSource,jdbcType=VARCHAR}, TRACE_PEOPLE = #{item.tracePeople,jdbcType=VARCHAR}, TP_CELL = #{item.tpCell,jdbcType=VARCHAR}, LOGO = #{item.logo,jdbcType=LONGVARCHAR}, PROVINCE = #{item.province,jdbcType=VARCHAR}, CITY = #{item.city,jdbcType=VARCHAR}, COUNTY = #{item.county,jdbcType=VARCHAR}, COMPANY_ADDRESS = #{item.companyAddress,jdbcType=VARCHAR}, ID_CARD = #{item.idCard,jdbcType=VARCHAR}, COMPANY_PHOTO = #{item.companyPhoto,jdbcType=LONGVARCHAR}, LONGITUDE = #{item.longitude,jdbcType=DECIMAL}, LATITUDE = #{item.latitude,jdbcType=DECIMAL}, AREA = #{item.area,jdbcType=DECIMAL}, REGISTER_TIME = #{item.registerTime,jdbcType=TIMESTAMP}, REGISTER_IP = #{item.registerIp,jdbcType=VARCHAR}, OPEN_TIME = #{item.openTime,jdbcType=TIMESTAMP}
WHERE ROW_ID = #{item.rowId,jdbcType=INTEGER}

UPDATE  HR_M_COMPANY
SET
DELETED_FLAG  =  '1'  WHERE DELETED_FLAG  =  '0' ${item}

mapper.select:
SELECT 

FROM HR_M_COMPANY WHERE DELETED_FLAG='0'  AND
ROW_ID = #{ rowId,jdbcType=INTEGER}
