mapper.update:
UPDATE SYS_L_IP_ADDRESSES
SORT_NBR = #{ sortNbr,jdbcType=INTEGER}, STATUS = #{ status,jdbcType=CHAR}, IP_COUNTRY = #{ ipCountry,jdbcType=VARCHAR}, IP_AREA = #{ ipArea,jdbcType=VARCHAR}, IP_PROVINCE = #{ ipProvince,jdbcType=VARCHAR}, IP_CITY = #{ ipCity,jdbcType=VARCHAR}, IP_COUNTY = #{ ipCounty,jdbcType=VARCHAR}, IP_ISP = #{ ipIsp,jdbcType=VARCHAR}, CREATE_ID = #{ createId,jdbcType=INTEGER}, CREATE_TIME = #{ createTime,jdbcType=TIMESTAMP}
WHERE IP = #{ ip,jdbcType=VARCHAR}

update.foreach:
UPDATE SYS_L_IP_ADDRESSES
SORT_NBR = #{item.sortNbr,jdbcType=INTEGER}, STATUS = #{item.status,jdbcType=CHAR}, IP_COUNTRY = #{item.ipCountry,jdbcType=VARCHAR}, IP_AREA = #{item.ipArea,jdbcType=VARCHAR}, IP_PROVINCE = #{item.ipProvince,jdbcType=VARCHAR}, IP_CITY = #{item.ipCity,jdbcType=VARCHAR}, IP_COUNTY = #{item.ipCounty,jdbcType=VARCHAR}, IP_ISP = #{item.ipIsp,jdbcType=VARCHAR}, CREATE_ID = #{item.createId,jdbcType=INTEGER}, CREATE_TIME = #{item.createTime,jdbcType=TIMESTAMP}
WHERE IP = #{item.ip,jdbcType=VARCHAR}

UPDATE  SYS_L_IP_ADDRESSES
SET
DELETED_FLAG  =  '1'  WHERE DELETED_FLAG  =  '0' ${item}

mapper.select:
SELECT 

FROM SYS_L_IP_ADDRESSES WHERE DELETED_FLAG='0'  AND
IP = #{ ip,jdbcType=VARCHAR}
